mapper.select:
select poa.*, mw.name, mw.category from polarity_analy poa inner join monitor_work mw on (poa.workId = mw.id) where mw.name like '%${workName}%' and poa.country like '%${country}%' and poa.platform like '%${platform}%'
and time = #{time}
order by poa.id

select date_tab.`year_date` as postTime, sum(positive) as positive,
  sum(negative) as negative, sum(neutrality) as neutrality
from
(SELECT DATE_FORMAT(#{startTime}, '%Y-%m-%d') AS `year_date`


        UNION SELECT DATE_FORMAT((#{startTime} + INTERVAL #{offset} DAY), '%Y-%m-%d') AS `year_date`


) as date_tab
left join (select * from polarity_analy where workId = #{workId}

    and country = #{country}

    ) as pa
on(pa.time = date_tab.`year_date`)
group by postTime
order by postTime asc

select date_tab.`year_month` as postTime, sum(positive) as positive,
       sum(negative) as negative, sum(neutrality) as neutrality
from (SELECT DATE_FORMAT(CURDATE(), '%Y-%m') AS `year_month`


            UNION SELECT DATE_FORMAT((CURDATE() - INTERVAL #{offset} MONTH), '%Y-%m') AS `year_month`


    ) as date_tab
         left join (select * from polarity_analy
            where 1 = 1

                 and country = #{country}


                  and workId = ${workId}

             ) as pa
             on(DATE_FORMAT(pa.time, "%Y-%m") = date_tab.`year_month`)
group by postTime
order by postTime

select country from (select country, sum(positive) as posCnt from polarity_analy
where workId = #{workId}
group by country having posCnt > 3 order by posCnt desc) as tab

select sum(positive) as positive, sum(negative) as negative, sum(neutrality) as neutrality
from polarity_analy
where DATE_FORMAT(time, '%Y-%m') = #{selectMonth}

        and workId = #{workId}


      and country =  #{country}
