DROP PROCEDURE IF EXISTS `hot_comments_proc`;
delimiter ;;
CREATE PROCEDURE `hot_comments_proc`(IN wId INT, IN cnt INT)
BEGIN
SELECT *
FROM raw_comment
WHERE workId = wId
ORDER BY likes DESC
LIMIT 0, cnt;
END
;;
